clear;close all;clc;A=1;time=5;fs=48000;t=0:1/fs:time;f=input('Podaj czestotliwosc lewego sygnalu: ');d=input('Podaj roznice czestotliwosci sygnalow: ');y_left=A*sin(2*pi*f*t);y_right=A*sin(2*pi*(f+d)*t);rumble=[y_left(:), y_right(:)];%z fouriera wychodzi sygnal prostokatnyN=200;fourier=zeros(1,(time*fs)+1);for k=1:N  fourier=fourier+(1/(2*k-1))*sin((2*k-1)*pi*t*f);endforfourier=(4/pi)*fourier;y_left=y_left.*fourier;y_right=y_right.*fourier;rumble=[y_left(:), y_right(:)];soundsc(rumble,fs);